GROUP BY sYear
ORDER BY sYear DESC;


-- Get the total sales based on product_code
SELECT p.Product_code, SUM(sales_amount) [Total Sales by Products] FROM transactions2 t
INNER JOIN products p
ON t.product_code = p.product_code
GROUP BY p.Product_code
ORDER BY p.Product_code;


-- Get the total sales based on markets_name
SELECT markets_name, SUM(sales_amount) [Total Sales by Products] FROM transactions2 t
INNER JOIN markets m
ON t.market_code = m.markets_code
GROUP BY markets_name
ORDER BY markets_name;


