-- Retrieve rolling total sales partitioned by product_code
SELECT d.sYear, p.Product_code, t.order_date, t.sales_amount, SUM(t.sales_amount) OVER (PARTITION BY p.product_code ORDER BY p.product_code, t.order_date) [Total Sales by Products] 
FROM transactions2 t
INNER JOIN products p ON t.product_code = p.product_code
INNER JOIN date_tbl d ON t.order_date = d.sDate
--WHERE d.sYear IN(2017,2018,2019,2020)
--GROUP BY d.sYear, p.Product_code
ORDER BY 1,2,3;


-- Get the total quantities of products sold for each year
SELECT d.sYear, p.Product_code, SUM(t.sales_qty) AS [Total Product Sold Per Year]
FROM transactions2 t
INNER JOIN products p ON t.product_code = p.product_code
INNER JOIN date_tbl d ON t.order_date = d.sDate
GROUP BY d.sYear, p.Product_code
ORDER BY 1, 3 DESC, 2;


-- Get the total sales of products for each year
SELECT d.sYear, p.Product_code, SUM(t.sales_amount) AS [Total Product Sales Per Year]
FROM transactions2 t
INNER JOIN products p ON t.product_code = p.product_code
INNER JOIN date_tbl d ON t.order_date = d.sDate
GROUP BY d.sYear, p.Product_code
ORDER BY 1,3 DESC,2;


-- Get the total sales of products in each market for each year
SELECT d.sYear, m.markets_name, p.Product_code, SUM(t.sales_amount) AS [Total Product Sales Per Year]
FROM transactions2 t
INNER JOIN products p ON t.product_code = p.product_code
INNER JOIN date_tbl d ON t.order_date = d.sDate
INNER JOIN markets m ON t.market_code = m.markets_code
GROUP BY d.sYear, m.markets_name, p.Product_code
ORDER BY 1, 2, 4 DESC;


-- Get the total sales of products in each zone for each year
SELECT d.sYear, m.zone, p.Product_code, SUM(t.sales_amount) AS [Total Product Sales Per Year]
FROM transactions2 t
INNER JOIN products p ON t.product_code = p.product_code
INNER JOIN date_tbl d ON t.order_date = d.sDate
INNER JOIN markets m ON t.market_code = m.markets_code
GROUP BY d.sYear, m.zone, p.Product_code
ORDER BY 1, 2, 4 DESC;
